css:
/*remover bot√£o cancelar pedido*/
.order-info__toggle {
    display: none !important;
}

.top-bar {
    display: none;
}

#product .prices__value {
    color: var(--secondary);
    font-size: 24 px;
}

.prices__value {
    color: var(--secondary);
    font-size: 16 px !important;
}

.product-card__name {
    --max-lines: 2;
    color: #000;
    font-size: 18 px !important;
}

.product-card__name:hover{
    color: #000;
    text-decoration: none !important;
}

.prices--big .prices__value {
    font-size: 24px !important;
    margin-bottom: var(--spacer-1);
}

@media (min-width: 992px) {
  .banner-slider {
    max-width: 100%;
    max-height: calc(100rem*var(--banner-aspect-ratio))
  }
}

.info-bar {
    width: 100%;
    max-width: 100rem;
    margin-left: auto;
    margin-right: auto;
    background: var(--secondary);
    color: var(--secondary-yiq);
}
